import React from 'react';
// import { } from 'bootstrap';
import GetWeather from '../component/GetWeather';
import '../../assect/css/mycss.css'


class Weather extends React.Component{
    constructor(props){
        super(props);
        this.state = {
            city: props.city,
            country: props.country,
            lon : undefined,
            lat : undefined,
            icon: undefined,
            main: undefined,
            description: undefined,
            celsius: undefined,
            temp_max : undefined,
            temp_min : undefined,
            pressure : undefined,
            humidity : undefined,
            wind_speed : undefined,
            visibility : undefined,
            rain : undefined,
            clouds : undefined,
            error : false
          };
    }



    calCelsius(temp){
        let cell = Math.floor(temp - 273.15);
        return cell
    }

    getWeather(e){
        var city = e.target.elements.city.value;
        var country = e.target.elements.country.value;
    }

    componentDidMount(){
        var city = this.setState(this.getWeather.city)
        // const country = e.target.elements.country.value;
        console.log(city )
        alert(city)
        fetch(`https://api.openweathermap.org/data/2.5/weather?q=London,uk&appid=f1f66ef328ad5089cd48bdf7d7cb81f6`)
        .then(response => response.json())
        .then(data => this.setState({
            city: data.name, 
            country: data.sys.country,
            lon : data.coord.lon,
            lat : data.coord.lat,
            icon: data.weather[0].icon,
            main: data.weather[0].main,
            description : data.weather[0].description,
            celsius : this.calCelsius(data.main.temp),
            temp_min : this.calCelsius(data.main.temp_min),
            temp_max : this.calCelsius(data.main.temp_max),
            pressure : data.main.pressure,
            humidity : data.main.humidity,
            visibility : data.visibility,
            wind_speed : data.wind.speed,

            rain : data.rain[1],
            clouds : data.clouds.all,



        },
             console.log(data)))
      }

    render(){
        const {city, country,lon, lat,  icon , main , description, celsius, temp_max, temp_min, pressure,
                     humidity, visibility ,wind_speed,rain, clouds, error} = this.state;
        const imgsrc= `http://openweathermap.org/img/wn/${icon}@2x.png`;
        return(

            <div className="App">

            <header className="App-header">
            <GetWeather />
              <h1>
                  { country}
                  </h1>   
                  <h4>
                  { city}
                  </h4>   
                  <img src = {imgsrc}/>    
                <h4>
                      {celsius} <br /> {description} <br /> {visibility} <br /> {clouds}
                </h4>       
            </header>
          </div>
        );
    }
}

export default Weather ;


