import React from 'react';
import logo from './logo.svg';
import './App.css';

class App extends React.Component {
  constructor(props){
    super(props);
    this.state = {
      userId : [],
      title : [],
      completed: []
    }
  }

  componentDidMount(){
    fetch('https://jsonplaceholder.typicode.com/todos')
    .then(response => response.json())
    // .then(json => console.log(json))
    .then(data => this.setState({userId: data.userId, titel: data.titel , completed: data.completed}))

  }
  render(){
    const {userId , titel , completed} = this.state;
    return (
      <div className="App">
        <header className="App-header">
          <img src={logo} className="App-logo" alt="logo" />
          <p>
            Edit <code>src/App.js</code> and save to reload.
          </p>
          <div>
            <h1>UserId</h1>
            {this.state.userId}
          </div>
          <div>
            <h1>completed</h1>
            {this.state.completed}
          </div>
          <div>
            <h1>title</h1>
            {this.state.title}
          </div>
        </header>
      </div>
    );
  }
}

export default App;
